import * as React from "react";
import AssemblyAI from "assemblyai";

const API_KEY = "YOUR_ASSEMBLYAI_API_KEY";
const FILE_URL =
  "https://storage.googleapis.com/aai-web-samples/5_common_sports_injuries.mp3";

class TranscriptionApp extends React.Component {
  runTranscription = async () => {
    const client = new AssemblyAI({ apiKey: API_KEY });

    const data = {
      audio_url: FILE_URL,
      speaker_labels: true,
    };

    try {
      const transcript = await client.transcripts.create(data);

      console.log(transcript.text);

      for (const utterance of transcript.utterances) {
        console.log(`Speaker ${utterance.speaker}: ${utterance.text}`);
      }
    } catch (error) {
      console.error("Transcription error:", error);
    }
  };

  render() {
    return (
      <div>
        <button onClick={this.runTranscription}>Transcribe and Display</button>
      </div>
    );
  }
}

export default TranscriptionApp;
