import React, { useEffect, useState } from "react";
import axios from "axios";

// ... (other imports and code)

const AudioTranscription = () => {
  const APIKey = "6bdccc2045804a77b4ac2a5ba59a0e28";
  const refreshInterval = 5000;

  const [transcriptText, setTranscriptText] = useState("");
  const [transcriptStatus, setTranscriptStatus] = useState("pending");
  const [uploadURL, setUploadURL] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      // Create an object URL for the selected file
      setUploadURL(URL.createObjectURL(selectedFile));
    }
  };

  useEffect(() => {
    if (!uploadURL) {
      return;
    }

    const assembly = axios.create({
      baseURL: "https://api.assemblyai.com/v2",
      headers: {
        authorization: APIKey,
        "content-type": "application/json",
      },
    });

    const createTranscriptionTask = async () => {
      try {
        // Create a transcription task using the upload URL
        const response = await assembly.post("/transcript", {
          upload_url: uploadURL,
        });
        console.log(response);
        // Store the task ID
        const taskId = response.data.id;

        // Interval for checking transcript completion
        const checkCompletionInterval = setInterval(async () => {
          const transcript = await assembly.get(`/transcript/${taskId}`);
          const transcriptStatus = transcript.data.status;

          if (transcriptStatus !== "completed") {
            setTranscriptStatus(transcriptStatus);
          } else {
            setTranscriptStatus("completed");
            clearInterval(checkCompletionInterval);
            setTranscriptText(transcript.data.text);
          }
        }, refreshInterval);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    createTranscriptionTask();
  }, [uploadURL]);

  return (
    <div>
      <h2>Transcription Status: {transcriptStatus}</h2>
      <input type="file" accept="audio/*" onChange={handleFileChange} />
      {uploadURL && <audio controls src={uploadURL} />}
      {transcriptStatus === "completed" && (
        <div>
          <h2>Transcription completed!</h2>
          <p>Your transcribed text:</p>
          <p>{transcriptText}</p>
        </div>
      )}
    </div>
  );
};

export default AudioTranscription;
