import React, { useState, useEffect } from "react";

const initialState = {
  url: null,
  blob: null,
  chunks: null,
  duration: {
    h: 0,
    m: 0,
    s: 0,
  },
};

function App() {
  const [audioDetails, setAudioDetails] = useState(initialState);
  const [transcript, setTranscript] = useState({ id: "" });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const interval = setInterval(async () => {
      if (transcript.id && transcript.status !== "completed" && isLoading) {
        try {
          const { data: transcriptData } = await assemblyApi.get(
            `/transcript/${transcript.id}`
          );
          setTranscript({ ...transcript, ...transcriptData });
        } catch (err) {
          console.error(err);
        }
      } else {
        setIsLoading(false);
        clearInterval(interval);
      }
    }, 1000);
    return () => clearInterval(interval);
  }, [isLoading, transcript]);

  const handleAudioStop = (data) => {
    setAudioDetails(data);
  };

  const handleReset = () => {
    setAudioDetails({ ...initialState });
    setTranscript({ id: "" });
  };

  const handleAudioUpload = async (audioFile) => {
    setIsLoading(true);

    const { data: uploadResponse } = await assemblyApi.post(
      "/upload",
      audioFile
    );

    const { data } = await assemblyApi.post("/transcript", {
      audio_url: uploadResponse.upload_url,
      speaker_labels: true,
      sentiment_analysis: true,
      entity_detection: true,
      iab_categories: true,
    });

    setTranscript({ id: data.id });
  };

  return (
    <div style={{ textAlign: "center", fontSize: "xl" }}>
      <div
        style={{
          minHeight: "50vh",
          padding: "30px",
        }}
      >
        <div style={{ marginBottom: "8px" }}>
          {transcript.text && transcript.status === "completed" ? (
            <Result transcript={transcript} />
          ) : (
            <Status isLoading={isLoading} status={transcript.status} />
          )}
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{ margin: "0 15px", display: "flex", alignItems: "center" }}
          >
            <div
              style={{
                width: "13px",
                height: "13px",
                backgroundColor: "lightgreen",
              }}
            ></div>
            <div style={{ fontSize: "sm", marginLeft: "-100px" }}>Positive</div>
          </div>

          <div
            style={{ margin: "0 15px", display: "flex", alignItems: "center" }}
          >
            <div
              style={{
                width: "13px",
                height: "13px",
                backgroundColor: "lightgray",
              }}
            ></div>
            <div style={{ fontSize: "sm" }}>Neutral</div>
          </div>

          <div
            style={{ margin: "0 15px", display: "flex", alignItems: "center" }}
          >
            <div
              style={{
                width: "13px",
                height: "13px",
                backgroundColor: "pink.200",
              }}
            ></div>
            <div style={{ fontSize: "sm" }}>Negative</div>
          </div>
        </div>

        <div style={{ width: "300px", paddingTop: "-1" }}>
          <Recorder
            record={true}
            audioURL={audioDetails.url}
            handleAudioStop={handleAudioStop}
            handleAudioUpload={handleAudioUpload}
            handleReset={handleReset}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
