import React, { useState } from "react";
import axios from "axios";

const AssemblyAIExample = () => {
  const [transcription, setTranscription] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  const apiKey = "6bdccc2045804a77b4ac2a5ba59a0e28"; // Replace with your AssemblyAI API key

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const transcribeAndDownload = async () => {
    if (!selectedFile) {
      alert("Please select an audio file.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      // Upload the selected file to AssemblyAI for transcription
      const uploadResponse = await axios.post(
        "https://api.assemblyai.com/v2/upload",
        formData,
        {
          headers: {
            Authorization: apiKey,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const { id } = uploadResponse.data;

      let completed = false;
      let transcript = "";

      while (!completed) {
        const transcriptResponse = await axios.get(
          `https://api.assemblyai.com/v2/transcript/${id}`,
          {
            headers: {
              Authorization: apiKey,
            },
          }
        );

        if (transcriptResponse.data.status === "completed") {
          completed = true;
          transcript = transcriptResponse.data.text;
        } else if (transcriptResponse.data.status === "failed") {
          throw new Error("Transcription failed. Please try another file.");
        } else {
          // Sleep or use a timer before the next poll (e.g., setTimeout)
          await new Promise((resolve) => setTimeout(resolve, 2000)); // Wait for 2 seconds before polling again
        }
      }

      setTranscription(transcript);

      // Create a Blob with the transcribed text and trigger a download
      const blob = new Blob([transcription], { type: "text/plain" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "transcription.txt";
      a.click();
    } catch (error) {
      console.error("Transcription error:", error);
      alert("An error occurred during transcription.");
    }
  };

  return (
    <div>
      <h1>Audio Transcription and Download</h1>
      <input type="file" accept=".mp3" onChange={handleFileChange} />
      <button onClick={transcribeAndDownload}>Transcribe & Download</button>
      <div>
        {transcription && (
          <div>
            <h2>Transcribed Text:</h2>
            <p>{transcription}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AssemblyAIExample;
