import React, { useState, useEffect } from "react";
import axios from "axios";

const assemblyApi = axios.create({
  baseURL: "https://api.assemblyai.com/v2",
  headers: {
    authorization: "6bdccc2045804a77b4ac2a5ba59a0e28", // Replace with your actual API key
    "content-type": "application/json",
  },
});

const initialState = {
  url: null,
  blob: null,
  chunks: null,
  duration: {
    h: 0,
    m: 0,
    s: 0,
  },
};

function App() {
  const [audioDetails, setAudioDetails] = useState(initialState);
  const [transcript, setTranscript] = useState({
    id: "",
    text: "",
    status: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const [audioFile, setAudioFile] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    // ... (unchanged code for interval polling)
  }, [isLoading, transcript]);

  const handleAudioStop = (data) => {
    setAudioDetails(data);
  };

  const handleReset = () => {
    setAudioDetails({ ...initialState });
    setTranscript({ id: "", text: "", status: "" });
    setError("");
  };

  const handleAudioUpload = (event) => {
    const file = event.target.files[0];
    setAudioFile(file);
  };

  const handleTranscribeAndDownload = async () => {
    if (audioFile) {
      setIsLoading(true);
      setError(""); // Reset any previous errors

      const formData = new FormData();
      formData.append("file", audioFile);

      try {
        const { data: uploadResponse } = await assemblyApi.post(
          "/upload",
          formData
        );

        const { data } = await assemblyApi.post("/transcript", {
          audio_url: uploadResponse.upload_url,
          speaker_labels: true,
          sentiment_analysis: true,
          entity_detection: true,
          iab_categories: true,
        });

        if (data.status === "completed") {
          // Transcription was successful
          const { data: transcriptData } = await assemblyApi.get(
            `/transcript/${data.id}`
          );
          setTranscript({
            id: data.id,
            text: transcriptData.text,
            status: data.status,
          });
        } else if (data.status === "failed") {
          // Transcription failed
          setTranscript({ id: data.id, text: "", status: data.status });
          setError("Transcription failed. Please try again.");
        } else {
          // Transcription is still in progress
          setTranscript({ id: data.id, text: "", status: data.status });
        }
      } catch (error) {
        setError("An error occurred during transcription.");
        console.error(error);
      } finally {
        setIsLoading(false);
      }
    } else {
      setError("Please select an audio file.");
    }
  };

  return (
    <div style={{ textAlign: "center", fontSize: "xl" }}>
      <div
        style={{
          minHeight: "50vh",
          padding: "30px",
        }}
      >
        <div style={{ marginBottom: "8px" }}>
          transcription stat
          {transcript.text ? (
            <div>
              <p>Transcription:</p>
              <p>{transcript.text}</p>
            </div>
          ) : (
            <p>
              {isLoading
                ? "Transcription in progress. Please wait..."
                : transcript.status === "failed"
                ? "Transcription failed. Please try again."
                : "Transcription not yet completed."}
            </p>
          )}
          {error && <p style={{ color: "red" }}>{error}</p>}
        </div>

        <div>
          <input type="file" accept=".mp3, .wav" onChange={handleAudioUpload} />
        </div>

        <div>
          <button onClick={handleTranscribeAndDownload}>
            Transcribe and Download
          </button>
          <button onClick={handleReset}>Reset</button>
        </div>

        <div style={{ width: "300px", paddingTop: "-1" }}>
          {/* Your Recorder component */}
        </div>
      </div>
    </div>
  );
}

export default App;
