import React, { useState, useEffect } from "react";
import axios from "axios";
import fs from "fs"; // Import the fs module for file reading

const baseUrl = "https://api.assemblyai.com/v2"; // Replace with the correct API endpoint
const apiKey = "your-api-key"; // Replace with your actual API key

const headers = {
  authorization: apiKey,
  "content-type": "audio/mpeg", // Set the correct content type for your audio file
};

const initialState = {
  url: null,
  blob: null,
  chunks: null,
  duration: {
    h: 0,
    m: 0,
    s: 0,
  },
};

function App() {
  const [audioDetails, setAudioDetails] = useState(initialState);
  const [transcript, setTranscript] = useState({
    id: "",
    text: "",
    status: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // ... (unchanged code for interval polling)
  }, [isLoading, transcript]);

  const checkTranscriptionStatus = async (transcriptionId) => {
    try {
      const { data } = await assemblyApi.get(`/transcript/${transcriptionId}`);
      console.log(data);
      if (data.status === "completed") {
        setTranscript({
          id: data.id,
          text: data.text,
          status: data.status,
        });
        console.log("completed");
        clearInterval(polling);
      } else if (data.status === "error") {
        setTranscript({ id: data.id, text: "", status: data.status });
        setError("Transcription failed. Please try again.");
        console.log("failed");
        clearInterval(polling);
      } else if (
        data.status === "queued" ||
        data.status === "processing" ||
        data.status === "throttled"
      ) {
        console.log("queued");
        setTranscript({ id: data.id, text: "", status: data.status });
      } else {
        console.log("else");
        setTranscript({ id: data.id, text, status: data.status });
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleAudioStop = (data) => {
    setAudioDetails(data);
  };

  const handleReset = () => {
    setAudioDetails({ ...initialState });
    setTranscript({ id: "", text: "", status: "" });
    setError("");
  };

  const handleAudioUpload = (event) => {
    const file = event.target.files[0];
    console.log(file);
    setAudioFile(file);
  };

  const handleTranscribeAndDownload = async () => {
    let polling;

    if (audioFile) {
      setIsLoading(true);
      setError(""); // Reset any previous errors

      // Read the audio data from the selected file
      fs.readFile(audioFile, async (err, data) => {
        if (err) {
          setError("Error reading audio file.");
          console.error(err);
          return;
        }

        try {
          // Send the audio data to the upload endpoint
          const uploadResponse = await axios.post(`${baseUrl}/upload`, data, {
            headers,
          });

          const { data } = await axios.post(`${baseUrl}/transcript`, {
            audio_url: uploadResponse.data.upload_url,
            speaker_labels: true,
            sentiment_analysis: true,
            entity_detection: true,
            iab_categories: true,
          });

          const pollingInterval = 5000; // Poll every 5 seconds
          polling = setInterval(
            () => checkTranscriptionStatus(data.id),
            pollingInterval
          );
        } catch (error) {
          setError("An error occurred during transcription.");
          console.error(error);
        } finally {
          setIsLoading(false);
        }
      });
    } else {
      setError("Please select an audio file.");
    }
  };

  return (
    <div style={{ textAlign: "center", fontSize: "xl" }}>
      <div
        style={{
          minHeight: "50vh",
          padding: "30px",
        }}
      >
        <div style={{ marginBottom: "8px" }}>
          {transcript.text ? (
            <div>
              <p>Transcription:</p>
              <p>{transcript.text}</p>
            </div>
          ) : (
            <p>
              {isLoading
                ? "Transcription in progress. Please wait..."
                : transcript.status === "failed"
                ? "Transcription failed. Please try again."
                : transcript.status === "queued"
                ? "Job in Queue, Please Wait..."
                : "Transcription not yet completed."}
            </p>
          )}
          {error && <p style={{ color: "red" }}>{error}</p>}
        </div>

        <div>
          <input type="file" accept=".mp3, .wav" onChange={handleAudioUpload} />
        </div>

        <div>
          <button onClick={handleTranscribeAndDownload}>
            Transcribe and Download
          </button>
          <button onClick={handleReset}>Reset</button>
        </div>

        <div style={{ width: "300px", paddingTop: "-1" }}>
          {/* Your Recorder component */}
        </div>
      </div>
    </div>
  );
}

export default App;
